mapper.select:
select

from t_cons_cart
where 1 = 1

	and goodsId = #{goodsid,jdbcType=VARCHAR}


	and customerNo = #{customerno,jdbcType=VARCHAR}


	and goodsSpecifications = #{goodsspecifications,jdbcType=VARCHAR}

select

from t_cons_cart
where 1 = 1 and customerNo = #{customerno,jdbcType=VARCHAR}
